import React, { useState } from 'react';
import { MessageCircle, ThumbsUp, X, Plus, MinusCircle } from 'lucide-react';

const CodeReviewComponent = () => {
  const [comments, setComments] = useState({});
  const [showAddComment, setShowAddComment] = useState(null);
  const [newComment, setNewComment] = useState('');

  // Sample code diff - in a real app, this would come from props
  const codeDiff = [
    { type: 'unchanged', content: 'function calculateTotal(items) {', lineNum: 1 },
    { type: 'removed', content: '  let total = 0;', lineNum: 2 },
    { type: 'added', content: '  const total = items.reduce((sum, item) => {', lineNum: 2 },
    { type: 'added', content: '    return sum + item.price * item.quantity;', lineNum: 3 },
    { type: 'added', content: '  }, 0);', lineNum: 4 },
    { type: 'unchanged', content: '  return total;', lineNum: 5 },
    { type: 'unchanged', content: '}', lineNum: 6 },
  ];

  const handleAddComment = (lineNum) => {
    if (newComment.trim()) {
      setComments(prev => ({
        ...prev,
        [lineNum]: [...(prev[lineNum] || []), {
          id: Date.now(),
          text: newComment,
          author: 'Current User',
          timestamp: new Date().toISOString(),
          resolved: false
        }]
      }));
      setNewComment('');
      setShowAddComment(null);
    }
  };

  const toggleResolveComment = (lineNum, commentId) => {
    setComments(prev => ({
      ...prev,
      [lineNum]: prev[lineNum].map(comment => 
        comment.id === commentId 
          ? { ...comment, resolved: !comment.resolved }
          : comment
      )
    }));
  };

  const deleteComment = (lineNum, commentId) => {
    setComments(prev => ({
      ...prev,
      [lineNum]: prev[lineNum].filter(comment => comment.id !== commentId)
    }));
  };

  const getLineColor = (type) => {
    switch (type) {
      case 'added':
        return 'bg-green-50';
      case 'removed':
        return 'bg-red-50';
      default:
        return 'bg-white';
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-4 border rounded-lg shadow bg-white">
      <div className="mb-4 p-2 bg-gray-100 rounded">
        <h2 className="text-lg font-semibold">Code Review</h2>
      </div>

      <div className="font-mono text-sm">
        {codeDiff.map((line, index) => (
          <div key={index} className="relative group">
            <div className={`flex ${getLineColor(line.type)} hover:bg-gray-50`}>
              <div className="w-12 text-gray-500 text-right pr-4 select-none">
                {line.lineNum}
              </div>
              <div className="flex-1 relative">
                <pre className="p-1">
                  <span className={line.type === 'added' ? 'text-green-700' : line.type === 'removed' ? 'text-red-700' : ''}>
                    {line.type === 'added' ? '+ ' : line.type === 'removed' ? '- ' : '  '}
                    {line.content}
                  </span>
                </pre>

                <button
                  onClick={() => setShowAddComment(line.lineNum)}
                  className="opacity-0 group-hover:opacity-100 absolute right-2 top-1/2 -translate-y-1/2 p-1 hover:bg-gray-200 rounded"
                >
                  <Plus size={16} />
                </button>
              </div>
            </div>

            {/* Comments Section */}
            {comments[line.lineNum]?.map(comment => (
              <div key={comment.id} className="ml-12 p-2 border-l-2 border-blue-200 bg-blue-50 mt-1">
                <div className="flex justify-between items-start">
                  <div>
                    <span className="font-semibold">{comment.author}</span>
                    <span className="text-gray-500 text-xs ml-2">
                      {new Date(comment.timestamp).toLocaleString()}
                    </span>
                  </div>
                  <div className="flex gap-2">
                    <button
                      onClick={() => toggleResolveComment(line.lineNum, comment.id)}
                      className={`p-1 rounded ${comment.resolved ? 'text-green-600' : 'text-gray-400'}`}
                    >
                      <ThumbsUp size={16} />
                    </button>
                    <button
                      onClick={() => deleteComment(line.lineNum, comment.id)}
                      className="p-1 rounded text-gray-400 hover:text-red-600"
                    >
                      <X size={16} />
                    </button>
                  </div>
                </div>
                <p className="mt-1 text-sm">{comment.text}</p>
              </div>
            ))}

            {/* Add Comment Form */}
            {showAddComment === line.lineNum && (
              <div className="ml-12 p-2 border-l-2 border-blue-200 bg-blue-50 mt-1">
                <textarea
                  value={newComment}
                  onChange={(e) => setNewComment(e.target.value)}
                  className="w-full p-2 border rounded resize-y"
                  placeholder="Add a comment..."
                  rows={3}
                />
                <div className="mt-2 flex gap-2">
                  <button
                    onClick={() => handleAddComment(line.lineNum)}
                    className="px-3 py-1 bg-blue-500 text-white rounded hover:bg-blue-600"
                  >
                    Add Comment
                  </button>
                  <button
                    onClick={() => setShowAddComment(null)}
                    className="px-3 py-1 bg-gray-200 rounded hover:bg-gray-300"
                  >
                    Cancel
                  </button>
                </div>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};
