import React, { useState } from 'react';
import { Upload, FileText, Loader } from 'lucide-react';
import CodeReviewComponent from './CodeReviewComponent';

const App = () => {
  const [files, setFiles] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const [isUploaded, setIsUploaded] = useState(false);
  const [vulnerabilities, setVulnerabilities] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleFileUpload = (event) => {
    const uploadedFile = event.target.files[0];
    if (uploadedFile && uploadedFile.name.endsWith('.zip')) {
      // In a real application, you would process the ZIP file here
      const simulatedJavaFiles = [
        {
          name: 'Test.java',
          content: `public class Test {
    public void getUserInput() {
        int userInput = Integer.parseInt(input);
    }

    public void accessArrayElement() {
        String[] array = new String[10];
        int index = 20;
        String s = array[index];
    }

    public void writeToTempFile() {
        File tempFile = new File(System.getProperty("java.io.tmpdir") + "/" + "filename");
    }
}`
        },
        {
          name: 'User.java',
          content: 'public class User {\n    private String name;\n    private String email;\n\n    public User(String name, String email) {\n        this.name = name;\n        this.email = email;\n    }\n}'
        }
      ];
      setFiles(simulatedJavaFiles);
      setIsUploaded(true);
    } else {
      alert('Please upload a ZIP file');
    }
  };

  const analyzeCode = async (content) => {
    setIsLoading(true);
    setError(null);

    try {
      const response = await fetch('http://localhost:8080/v2/vul', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify({
          code: content
        })
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const vulnerabilityData = await response.json();
      console.log('Vulnerabilities found:', vulnerabilityData);
      setVulnerabilities(vulnerabilityData);
    } catch (error) {
      console.error('Error analyzing code:', error);
      setError(error.message);
      setVulnerabilities([]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleFileSelect = (file) => {
    setSelectedFile(file);
    setVulnerabilities([]); // Clear previous vulnerabilities
    setError(null); // Clear previous errors
    analyzeCode(file.content);
  };

  const getCodeDiff = (content) => {
    const lines = content.split('\n');
    return lines.map((line, index) => {
      const lineNumber = (index + 1).toString();
      const vulnerability = vulnerabilities.find(v => v.line_number === lineNumber);

      return {
        type: vulnerability ? 'vulnerable' : 'unchanged',
        content: line.trim(),
        lineNum: parseInt(lineNumber),
        vulnerability: vulnerability
      };
    });
  };

  if (!isUploaded) {
    return (
      <div className="min-h-screen bg-gray-50 flex flex-col items-center justify-center p-4">
        <div className="max-w-xl w-full bg-white rounded-lg shadow-lg p-8">
          <h1 className="text-2xl font-bold text-center mb-8">
            Upload Your Java Project
          </h1>

          <div className="border-2 border-dashed border-gray-300 rounded-lg p-8 text-center">
            <Upload className="mx-auto h-12 w-12 text-gray-400 mb-4" />
            <label className="block">
              <span className="sr-only">Choose ZIP file</span>
              <input
                type="file"
                className="block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
                accept=".zip"
                onChange={handleFileUpload}
              />
            </label>
            <p className="mt-2 text-sm text-gray-500">
              Please upload your Java project as a ZIP file
            </p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 flex">
      {/* Sidebar */}
      <div className="w-64 bg-white shadow-lg">
        <div className="p-4 border-b">
          <h2 className="text-lg font-semibold">Java Files</h2>
        </div>
        <div className="overflow-y-auto">
          {files.map((file, index) => (
            <button
              key={index}
              onClick={() => handleFileSelect(file)}
              className={`w-full px-4 py-2 text-left flex items-center space-x-2 hover:bg-gray-50 ${
                selectedFile?.name === file.name ? 'bg-blue-50' : ''
              }`}
            >
              <FileText size={16} />
              <span>{file.name}</span>
            </button>
          ))}
        </div>
      </div>

      {/* Main content */}
      <div className="flex-1 overflow-auto p-4">
        {isLoading ? (
          <div className="h-full flex items-center justify-center">
            <Loader className="w-8 h-8 animate-spin text-blue-500" />
          </div>
        ) : error ? (
          <div className="h-full flex items-center justify-center">
            <div className="text-red-500 text-center">
              <p className="font-semibold">Error analyzing code:</p>
              <p className="mt-2">{error}</p>
            </div>
          </div>
        ) : selectedFile ? (
          <div>
            {vulnerabilities.length > 0 && (
              <div className="mb-4 p-4 bg-yellow-50 border border-yellow-200 rounded-lg">
                <h3 className="font-semibold text-yellow-800">
                  {vulnerabilities.length} potential vulnerabilities found
                </h3>
                <div className="mt-2 space-y-2">
                  {vulnerabilities.map((vuln, index) => (
                    <div key={index} className="text-sm">
                      <span className="font-medium text-yellow-900">Line {vuln.line_number}:</span>{' '}
                      <span className="text-yellow-800">{vuln.description}</span>
                    </div>
                  ))}
                </div>
              </div>
            )}
            <CodeReviewComponent 
              codeDiff={getCodeDiff(selectedFile.content)} 
              vulnerabilities={vulnerabilities}
            />
          </div>
        ) : (
          <div className="h-full flex items-center justify-center text-gray-500">
            Select a file to review
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
